/**
 * üéØ Vistara UI - Main App Component
 * "Command your Design."
 */

import React, { useState } from 'react';
import Showcase from './components/Showcase';

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleTheme = () => {
    setDarkMode(!darkMode);
    document.documentElement.setAttribute('data-theme', darkMode ? 'light' : 'dark');
  };

  return (
    <div className="min-h-screen" style={{ 
      backgroundColor: 'var(--color-background-primary)',
      color: 'var(--color-text-primary)'
    }}>
      {/* Header */}
      <header className="vistara-surface p-6 mb-8">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold" style={{ color: 'var(--color-primary)' }}>
              Vistara UI
            </h1>
            <p className="text-lg" style={{ color: 'var(--color-text-secondary)' }}>
              Command your Design.
            </p>
          </div>

          {/* Theme Toggle */}
          <button
            onClick={toggleTheme}
            className="px-4 py-2 rounded-lg border transition-all duration-200"
            style={{
              backgroundColor: 'var(--color-surface)',
              borderColor: 'var(--color-border-medium)',
              color: 'var(--color-text-primary)'
            }}
          >
            {darkMode ? '‚òÄÔ∏è Light' : 'üåô Dark'}
          </button>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-6">
        <Showcase />
      </main>

      {/* Footer */}
      <footer className="mt-16 p-6 text-center" style={{ color: 'var(--color-text-muted)' }}>
        <p>Built with ‚ù§Ô∏è by Falcon ü¶Ö | AI-First UI Development</p>
      </footer>
    </div>
  );
}

export default App;
